!function(){"use strict";angular.module("app.services").factory("CategoryService",["ParseRecord",function(e){return e.extend("Category",["subdomain","name"],{priceWithTaxes:function(){return 1.2*this.price}},{getFromCategory:function(e){return this.query().equalTo("category",e).find()},getById:function(e){return this.query().equalTo("objectId",e).find()},getPromotions:function(e){return this.query().greaterThan("discount",e).find()}})}])}();