!function(){"use strict";angular.module("app.services").factory("ProductService",["ParseRecord",function(e){return e.extend("Product",["category","name","price"],{priceWithTaxes:function(){return 1.2*this.price}},{getById:function(e){return this.query().equalTo("objectId",e).find()},getInCategory:function(e){return this.query().equalTo("category",{__type:"Pointer",className:"Category",objectId:e}).find()}})}]).factory("ProductImageService",["ParseRecord",function(e){return e.extend("ProductImage",["brand","product"],{priceWithTaxes:function(){return 1.2*this.price}},{getById:function(e){return this.query().equalTo("objectId",e).find()},getAllProductImagesForAllBrands:function(e){return this.query().equalTo("product",{__type:"Pointer",className:"Product",objectId:e}).find()}})}])}();